package corejava;

public class Break_switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int age =12;
  switch (age) {
  case 1:
	  System.out.println("u can walk");
	  break;
  case 2:
	  System.out.println("u can talk");
	  break;
  case 3:
	  System.out.println("u can run");
	  break;
  case 4:
	  System.out.println("u can sing");
	  break;
  case 5:
	  System.out.println("u can read");
	  break;
  case 6:
	  System.out.println("u can write");
	  break;
  case 7:
	  System.out.println("u can play");
	  break;
  case 8:
	  System.out.println("u can jump");
	  break;
  case 9:
	  System.out.println("u can ride a cycle");
	  break;
  case 10:
	  System.out.println("u can ride a bike");
	  break;
  
  }
  if (age==11)
  {
	  System.out.println("u can go to school");
  }
  else
  {
	  System.out.println("i don't know u r age");
  }
	}

}
